import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './IncidentDetails.css';
import locationIcon from '../assets/icons/location.png';
import imageIcon from '../assets/icons/image.png';
import audioIcon from '../assets/icons/audio.png';
import videoIcon from '../assets/icons/video.png';

const IncidentDetails = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const incidentType = location.state?.incidentType || 'Incident';

  const [formData, setFormData] = useState({
    description: '',
    location: '',
    images: [],
    audio: [],
    video: []
  });

  const [currentFileIndices, setCurrentFileIndices] = useState({
    images: 0,
    audio: 0,
    video: 0
  });

  const [errors, setErrors] = useState({
    description: '',
    files: ''
  });

  const [previews, setPreviews] = useState({
    images: [],
    video: []
  });

  const validateForm = () => {
    let isValid = true;
    const newErrors = { description: '', files: '' };

    if (!formData.description.trim()) {
      newErrors.description = 'Description is required';
      isValid = false;
    }

    if (formData.images.length === 0 && formData.audio.length === 0 && formData.video.length === 0) {
      newErrors.files = 'Please upload at least one file (image, audio, or video)';
      isValid = false;
    }

    setErrors(newErrors);
    return isValid;
  };

  const handleFileUpload = (type, e) => {
    const files = Array.from(e.target.files);
    const maxSizes = {
      images: 25,
      audio: 5,
      video: 30
    };

    const totalSize = files.reduce((sum, file) => sum + file.size / (1024 * 1024), 0);
    
    if (totalSize > maxSizes[type]) {
      setErrors({ ...errors, files: `Total ${type} size exceeds ${maxSizes[type]}MB limit` });
      return;
    }

    setFormData(prev => ({
      ...prev,
      [type]: [...prev[type], ...files]
    }));

    // Generate previews for images and videos
    if (type === 'images' || type === 'video') {
      const newPreviews = files.map(file => URL.createObjectURL(file));
      setPreviews(prev => ({
        ...prev,
        [type]: [...(prev[type] || []), ...newPreviews]
      }));
    }

    setErrors({ ...errors, files: '' });
  };

  const removeFile = (type, index) => {
    setFormData(prev => ({
      ...prev,
      [type]: prev[type].filter((_, i) => i !== index)
    }));
    
    if (type === 'images' || type === 'video') {
      setPreviews(prev => ({
        ...prev,
        [type]: prev[type].filter((_, i) => i !== index)
      }));
    }

    // Adjust current index if needed
    setCurrentFileIndices(prev => ({
      ...prev,
      [type]: Math.max(0, Math.min(prev[type], formData[type].length - 2))
    }));
  };

  const navigateFile = (type, direction) => {
    setCurrentFileIndices(prev => ({
      ...prev,
      [type]: direction === 'next' 
        ? Math.min(prev[type] + 1, formData[type].length - 1)
        : Math.max(prev[type] - 1, 0)
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    // Here you would typically send the data to your backend
    console.log('Submitting form data:', formData);
    
    // For demonstration purposes, show success and navigate back
    alert('Report submitted successfully!');
    navigate(-1);
  };

  const renderFilePreview = (type, file, index, total) => {
    const isImage = type === 'images';
    const isVideo = type === 'video';
    const currentIndex = currentFileIndices[type];
    const previewUrl = type === 'images' ? previews.images[currentIndex] : 
                      type === 'video' ? previews.video[currentIndex] : null;

    if (index !== currentIndex) return null;

    return (
      <div className="file-preview-container" key={index}>
        <div className="file-preview">
          {isImage && (
            <img src={previewUrl} alt={`Preview ${index + 1}`} />
          )}
          {isVideo && (
            <video 
              src={previewUrl} 
              controls
              style={{ width: '100%', height: '100%', objectFit: 'cover' }}
            />
          )}
          {!isImage && !isVideo && (
            <div style={{ 
              width: '100%', 
              height: '100%', 
              background: '#f0f0f0',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              {file.name}
            </div>
          )}
          
          {(isVideo || type === 'audio') && !previewUrl && (
            <div className="play-overlay">
              <svg viewBox="0 0 24 24">
                <path d="M8 5v14l11-7z"/>
              </svg>
            </div>
          )}
          
          <div className="file-counter">
            {index + 1}/{total}
          </div>

          {total > 1 && (
            <>
              {currentIndex > 0 && (
                <button 
                  type="button" 
                  className="nav-button prev"
                  onClick={() => navigateFile(type, 'prev')}
                >
                  <svg viewBox="0 0 24 24">
                    <path d="M15.41 7.41L14 6l-6 6 6 6 1.41-1.41L10.83 12z"/>
                  </svg>
                </button>
              )}
              {currentIndex < total - 1 && (
                <button 
                  type="button" 
                  className="nav-button next"
                  onClick={() => navigateFile(type, 'next')}
                >
                  <svg viewBox="0 0 24 24">
                    <path d="M10 6L8.59 7.41 13.17 12l-4.58 4.59L10 18l6-6z"/>
                  </svg>
                </button>
              )}
            </>
          )}
        </div>
        
        <button 
          type="button" 
          className="delete-button"
          onClick={() => removeFile(type, currentIndex)}
        >
          <svg viewBox="0 0 24 24">
            <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
          </svg>
        </button>
      </div>
    );
  };

  return (
    <div className="incident-details-container">
      <div className="incident-details-card">
        <div className="incident-header">
          <button className="back-button" onClick={() => navigate(-1)}>
            <svg width="24" height="24" viewBox="0 0 24 24">
              <path d="M20 11H7.83l5.59-5.59L12 4l-8 8 8 8 1.41-1.41L7.83 13H20v-2z" fill="white"/>
            </svg>
          </button>
          <h1>{incidentType}</h1>
        </div>

        <form onSubmit={handleSubmit} className="incident-form">
          <div className="description-input">
            <input
              type="text"
              placeholder="Enter Description"
              value={formData.description}
              onChange={(e) => {
                setFormData({ ...formData, description: e.target.value });
                if (errors.description) setErrors({ ...errors, description: '' });
              }}
            />
            {errors.description && <div className="error-message">{errors.description}</div>}
          </div>

          <div className="location-selector">
            <img src={locationIcon} alt="Location" className="location-icon" />
            <span className="location-text">Choose Incident Location</span>
          </div>

          <div className="preview-section">
            {formData.images.length > 0 && formData.images.map((file, index) => 
              renderFilePreview('images', file, index, formData.images.length)
            )}
            
            {formData.audio.length > 0 && formData.audio.map((file, index) => 
              renderFilePreview('audio', file, index, formData.audio.length)
            )}
            
            {formData.video.length > 0 && formData.video.map((file, index) => 
              renderFilePreview('video', file, index, formData.video.length)
            )}
          </div>

          <div className="upload-sections">
            <div className="upload-item">
              <div className="file-type-label">
                <img src={imageIcon} alt="Images" />
                <span>Image - {formData.images.length} Files</span>
              </div>
              <span className="file-size">
                {(formData.images.reduce((sum, file) => sum + file.size / (1024 * 1024), 0)).toFixed(2)} MB / 25
              </span>
              <input
                type="file"
                accept="image/*"
                multiple
                onChange={(e) => handleFileUpload('images', e)}
                style={{ display: 'none' }}
                id="image-upload"
              />
              <label htmlFor="image-upload" style={{ cursor: 'pointer' }}>
                Choose Files
              </label>
            </div>

            <div className="upload-item">
              <div className="file-type-label">
                <img src={audioIcon} alt="Audio" />
                <span>Audio - {formData.audio.length} Files</span>
              </div>
              <span className="file-size">
                {(formData.audio.reduce((sum, file) => sum + file.size / (1024 * 1024), 0)).toFixed(2)} MB / 5
              </span>
              <input
                type="file"
                accept="audio/*"
                multiple
                onChange={(e) => handleFileUpload('audio', e)}
                style={{ display: 'none' }}
                id="audio-upload"
              />
              <label htmlFor="audio-upload" style={{ cursor: 'pointer' }}>
                Choose Files
              </label>
            </div>

            <div className="upload-item">
              <div className="file-type-label">
                <img src={videoIcon} alt="Video" />
                <span>Video - {formData.video.length} Files</span>
              </div>
              <span className="file-size">
                {(formData.video.reduce((sum, file) => sum + file.size / (1024 * 1024), 0)).toFixed(2)} MB / 30
              </span>
              <input
                type="file"
                accept="video/*"
                multiple
                onChange={(e) => handleFileUpload('video', e)}
                style={{ display: 'none' }}
                id="video-upload"
              />
              <label htmlFor="video-upload" style={{ cursor: 'pointer' }}>
                Choose Files
              </label>
            </div>
          </div>

          <button type="submit" className="submit-button">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default IncidentDetails;
