import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { incidentService } from '../services/api';
import './IncidentDetails.css';

// Using emoji as temporary icons
const ICONS = {
  location: 'üìç',
  image: 'üñºÔ∏è',
  audio: 'üéµ',
  video: 'üé•'
};

const IncidentDetails = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const [formData, setFormData] = useState({
    type: location.state?.incidentType || '',
    description: '',
    location: {
      coordinates: [],
      address: ''
    },
    media: {
      images: [],
      audio: [],
      video: []
    }
  });

  const [currentFileIndices, setCurrentFileIndices] = useState({
    images: 0,
    audio: 0,
    video: 0
  });

  const [previews, setPreviews] = useState({
    images: [],
    video: []
  });

  const handleBack = () => {
    const fromReportIncident = location.state?.fromReportIncident;
    if (fromReportIncident) {
      navigate('/home');
    } else {
      navigate('/report-incident');
    }
  };

  const handleDescriptionChange = (e) => {
    setFormData(prev => ({
      ...prev,
      description: e.target.value
    }));
  };

  const handleLocationSelect = async () => {
    try {
      // For demo purposes using Kathmandu coordinates
      const coordinates = [27.7172, 85.3240];
      const address = 'Kathmandu, Nepal';
      
      setFormData(prev => ({
        ...prev,
        location: {
          coordinates,
          address
        }
      }));
    } catch (error) {
      setError('Failed to get location. Please try again.');
    }
  };

  const handleFileUpload = (type, files) => {
    const allowedFiles = Array.from(files).filter(file => {
      const size = file.size / (1024 * 1024); // Convert to MB
      const maxSize = type === 'video' ? 50 : 10;
      return size <= maxSize;
    });

    if (allowedFiles.length < files.length) {
      setError(`Some files were too large. Maximum size: ${type === 'video' ? '50MB' : '10MB'}`);
      return;
    }

    const fileArray = allowedFiles.map(file => ({
      url: URL.createObjectURL(file),
      filename: file.name,
      size: file.size,
      file // Keep the actual file for upload
    }));

    setFormData(prev => ({
      ...prev,
      media: {
        ...prev.media,
        [type]: [...prev.media[type], ...fileArray]
      }
    }));

    // Update previews for images and videos
    if (type === 'images' || type === 'video') {
      setPreviews(prev => ({
        ...prev,
        [type]: [...prev[type], ...fileArray.map(f => f.url)]
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!formData.location.coordinates || formData.location.coordinates.length !== 2) {
      setError('Please select a location');
      setLoading(false);
      return;
    }

    try {
      // Create FormData object for file upload
      const submitData = new FormData();
      submitData.append('type', formData.type);
      submitData.append('description', formData.description);
      submitData.append('location', JSON.stringify(formData.location));
      
      // Append files
      Object.entries(formData.media).forEach(([type, files]) => {
        files.forEach(fileObj => {
          if (fileObj.file) {
            submitData.append(type, fileObj.file);
          }
        });
      });

      const response = await incidentService.createIncident(submitData);
      console.log('Incident created:', response);
      navigate('/home', { state: { incidentCreated: true } });
    } catch (err) {
      setError(err.message || 'Failed to create incident. Please try again.');
      console.error('Error creating incident:', err);
    } finally {
      setLoading(false);
    }
  };

  const renderFilePreview = (type, file, index, total) => {
    const isImage = type === 'images';
    const isVideo = type === 'video';
    const currentIndex = currentFileIndices[type];
    const previewUrl = type === 'images' ? previews.images[currentIndex] : 
                      type === 'video' ? previews.video[currentIndex] : null;

    if (index !== currentIndex) return null;

    return (
      <div className="file-preview-container" key={index}>
        <div className="file-preview">
          {isImage && (
            <img src={previewUrl} alt={`Preview ${index + 1}`} />
          )}
          {isVideo && (
            <video 
              src={previewUrl} 
              controls
              style={{ width: '100%', height: '100%', objectFit: 'cover' }}
            />
          )}
          {!isImage && !isVideo && (
            <div style={{ 
              width: '100%', 
              height: '100%', 
              background: '#f0f0f0',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              {file.name}
            </div>
          )}
          
          {(isVideo || type === 'audio') && !previewUrl && (
            <div className="play-overlay">
              <svg viewBox="0 0 24 24">
                <path d="M8 5v14l11-7z"/>
              </svg>
            </div>
          )}
          
          <div className="file-counter">
            {index + 1}/{total}
          </div>

          {total > 1 && (
            <>
              {currentIndex > 0 && (
                <button 
                  type="button" 
                  className="nav-button prev"
                  onClick={() => navigateFile(type, 'prev')}
                >
                  <svg viewBox="0 0 24 24">
                    <path d="M15.41 7.41L14 6l-6 6 6 6 1.41-1.41L10.83 12z"/>
                  </svg>
                </button>
              )}
              {currentIndex < total - 1 && (
                <button 
                  type="button" 
                  className="nav-button next"
                  onClick={() => navigateFile(type, 'next')}
                >
                  <svg viewBox="0 0 24 24">
                    <path d="M10 6L8.59 7.41 13.17 12l-4.58 4.59L10 18l6-6z"/>
                  </svg>
                </button>
              )}
            </>
          )}
        </div>
        
        <button 
          type="button" 
          className="delete-button"
          onClick={() => removeFile(type, currentIndex)}
        >
          <svg viewBox="0 0 24 24">
            <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
          </svg>
        </button>
      </div>
    );
  };

  const navigateFile = (type, direction) => {
    setCurrentFileIndices(prev => ({
      ...prev,
      [type]: direction === 'next' 
        ? Math.min(prev[type] + 1, formData.media[type].length - 1)
        : Math.max(prev[type] - 1, 0)
    }));
  };

  const removeFile = (type, index) => {
    setFormData(prev => ({
      ...prev,
      media: {
        ...prev.media,
        [type]: prev.media[type].filter((_, i) => i !== index)
      }
    }));
    
    if (type === 'images' || type === 'video') {
      setPreviews(prev => ({
        ...prev,
        [type]: prev[type].filter((_, i) => i !== index)
      }));
    }

    // Adjust current index if needed
    setCurrentFileIndices(prev => ({
      ...prev,
      [type]: Math.max(0, Math.min(prev[type], formData.media[type].length - 2))
    }));
  };

  return (
    <div className="incident-details-container">
      <div className="incident-details-card">
        <div className="incident-header">
          <button className="back-button" onClick={handleBack}>
            <span>‚Üê</span>
          </button>
          <h2>{formData.type || 'Report Incident'}</h2>
        </div>

        <form className="incident-form" onSubmit={handleSubmit}>
          <textarea
            className="description-input"
            placeholder="Enter Description"
            value={formData.description}
            onChange={handleDescriptionChange}
            required
          />

          <button
            type="button"
            className="location-selector"
            onClick={handleLocationSelect}
          >
            <span className="icon">{ICONS.location}</span>
            {formData.location.address || 'Choose Incident Location'}
          </button>

          <div className="upload-sections">
            <div className="upload-section">
              <label>
                <span className="upload-icon">{ICONS.image}</span>
                Images (Max 10MB each)
                <input
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={(e) => handleFileUpload('images', e.target.files)}
                />
              </label>
            </div>

            <div className="upload-section">
              <label>
                <span className="upload-icon">{ICONS.audio}</span>
                Audio (Max 10MB each)
                <input
                  type="file"
                  accept="audio/*"
                  multiple
                  onChange={(e) => handleFileUpload('audio', e.target.files)}
                />
              </label>
            </div>

            <div className="upload-section">
              <label>
                <span className="upload-icon">{ICONS.video}</span>
                Video (Max 50MB each)
                <input
                  type="file"
                  accept="video/*"
                  multiple
                  onChange={(e) => handleFileUpload('video', e.target.files)}
                />
              </label>
            </div>
          </div>

          <div className="preview-section">
            {formData.media.images.length > 0 && formData.media.images.map((file, index) => 
              renderFilePreview('images', file, index, formData.media.images.length)
            )}
            
            {formData.media.audio.length > 0 && formData.media.audio.map((file, index) => 
              renderFilePreview('audio', file, index, formData.media.audio.length)
            )}
            
            {formData.media.video.length > 0 && formData.media.video.map((file, index) => 
              renderFilePreview('video', file, index, formData.media.video.length)
            )}
          </div>

          {error && <div className="error-message">{error}</div>}

          <button
            type="submit"
            className="submit-button"
            disabled={loading}
          >
            {loading ? 'Submitting...' : 'Submit'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default IncidentDetails;
