import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import MarkerClusterGroup from 'react-leaflet-cluster';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './BloodBank.css';

// Fix for default markers in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Custom blood bank marker icon
const createBloodBankIcon = (count) => {
  return new L.DivIcon({
    className: 'custom-blood-bank-marker',
    html: `<div style="
      background: #2196F3;
      color: white;
      border-radius: 50%;
      width: 35px;
      height: 35px;
      display: flex;
      align-items: center;
      justify-content: center;
      font-weight: bold;
      border: 3px solid white;
      box-shadow: 0 3px 6px rgba(0,0,0,0.3);
      font-size: 12px;
    ">${count || '+'}</div>`,
    iconSize: [35, 35],
    iconAnchor: [17, 17]
  });
};

// Sample blood bank locations in Nepal (expanded for clustering demo)
const bloodBankLocations = [
  { id: 1, name: 'Central Blood Bank', address: 'Kathmandu, Nepal', phone: '01-4225344', position: [27.7172, 85.3240], count: '15' },
  { id: 2, name: 'Bir Hospital Blood Bank', address: 'Mahaboudha, Kathmandu', phone: '01-4221119', position: [27.7056, 85.3137], count: '19' },
  { id: 3, name: 'Teaching Hospital Blood Bank', address: 'Maharajgunj, Kathmandu', phone: '01-4412303', position: [27.7394, 85.3350], count: '12' },
  { id: 4, name: 'Patan Hospital Blood Bank', address: 'Lagankhel, Lalitpur', phone: '01-5522266', position: [27.6588, 85.3247], count: '8' },
  { id: 5, name: 'Bhaktapur Hospital Blood Bank', address: 'Bhaktapur, Nepal', phone: '01-6610798', position: [27.6710, 85.4298], count: '6' },
  { id: 6, name: 'Tribhuvan University Blood Bank', address: 'Kirtipur, Kathmandu', phone: '01-4330433', position: [27.6780, 85.2800], count: '11' },
  { id: 7, name: 'Nepal Red Cross Blood Bank', address: 'Kalimati, Kathmandu', phone: '01-4270650', position: [27.6950, 85.3100], count: '22' },
  { id: 8, name: 'Manmohan Hospital Blood Bank', address: 'Swoyambhu, Kathmandu', phone: '01-4271595', position: [27.7150, 85.2900], count: '7' },
  { id: 9, name: 'Dhulikhel Hospital Blood Bank', address: 'Dhulikhel, Kavre', phone: '011-490497', position: [27.6200, 85.5400], count: '5' },
  { id: 10, name: 'Chitwan Medical College Blood Bank', address: 'Bharatpur, Chitwan', phone: '056-526006', position: [27.6800, 84.4300], count: '9' },
  { id: 11, name: 'Pokhara Hospital Blood Bank', address: 'Pokhara, Kaski', phone: '061-465482', position: [28.2096, 83.9856], count: '13' },
  { id: 12, name: 'Lumbini Provincial Hospital Blood Bank', address: 'Butwal, Rupandehi', phone: '071-547722', position: [27.7000, 83.4486], count: '8' },
];

const BloodBank = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [userLocation, setUserLocation] = useState(null);

  const handleBack = () => {
    navigate('/emergency-contact');
  };

  const handleCall = (phone) => {
    window.location.href = `tel:${phone}`;
  };

  const handleLocationClick = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation([position.coords.latitude, position.coords.longitude]);
        },
        (error) => {
          alert('Unable to get your location. Please enable location services.');
        }
      );
    } else {
      alert('Geolocation is not supported by this browser.');
    }
  };

  const handleListClick = () => {
    navigate('/blood-bank-list');
  };

  // Filter blood banks based on search query
  const filteredBloodBanks = bloodBankLocations.filter(bank =>
    bank.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    bank.address.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Default center (Kathmandu, Nepal)
  const mapCenter = userLocation || [27.7172, 85.3240];

  return (
    <div className="blood-bank-container">
      <div className="blood-bank-card">
        <div className="blood-bank-header">
          <button className="blood-bank-back-btn" onClick={handleBack}>
            <svg width="24" height="24" viewBox="0 0 24 24">
              <path d="M20 11H7.83l5.59-5.59L12 4l-8 8 8 8 1.41-1.41L7.83 13H20v-2z" fill="white"/>
            </svg>
          </button>
          <h1>Blood Bank</h1>
          <div className="blood-bank-spacer"></div>
        </div>

        <div className="blood-bank-map-container">
          <div style={{
            height: '500px',
            width: '100%',
            position: 'relative',
            background: '#f0f0f0',
            border: '1px solid #ddd'
          }}>
            <MapContainer
              center={mapCenter}
              zoom={12}
              style={{ height: '100%', width: '100%', zIndex: 1 }}
              className="blood-bank-map"
            >
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              {bloodBankLocations.map((bank) => (
                <Marker
                  key={bank.id}
                  position={bank.position}
                >
                  <Popup>
                    <div className="blood-bank-popup">
                      <h3>{bank.name}</h3>
                      <p><strong>Address:</strong> {bank.address}</p>
                      <p><strong>Phone:</strong> {bank.phone}</p>
                      <button
                        className="blood-bank-call-btn"
                        onClick={() => handleCall(bank.phone)}
                      >
                        Call Now
                      </button>
                    </div>
                  </Popup>
                </Marker>
              ))}
              {userLocation && (
                <Marker position={userLocation}>
                  <Popup>Your Location</Popup>
                </Marker>
              )}
            </MapContainer>
          </div>

          <button className="blood-bank-location-btn" onClick={handleLocationClick}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path d="M12 2C8.13 2 5 5.13 5 9c0 5.25 7 13 7 13s7-7.75 7-13c0-3.87-3.13-7-7-7zm0 9.5c-1.38 0-2.5-1.12-2.5-2.5s1.12-2.5 2.5-2.5 2.5 1.12 2.5 2.5-1.12 2.5-2.5 2.5z" fill="white"/>
            </svg>
          </button>

          <button className="blood-bank-list-btn" onClick={handleListClick}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path d="M3 13h2v-2H3v2zm0 4h2v-2H3v2zm0-8h2V7H3v2zm4 4h14v-2H7v2zm0 4h14v-2H7v2zM7 7v2h14V7H7z" fill="white"/>
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default BloodBank;
