import React, { useState } from "react";
import "./Login.css"; // Import the CSS file for styling

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    rememberMe: false
  });

  const [showPassword, setShowPassword] = useState(false);
  const [logoError, setLogoError] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your login logic here
    console.log('Login attempt with:', formData);
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className="login-container">
      <div className="skip-button">
        <button onClick={() => console.log('Skip clicked')}>Skip</button>
      </div>
      
      <div className="login-box">
        <div className="login-header">
          {!logoError ? (
            <img 
              src={require("../assets/logo.png")} 
              alt="Nepal Police Logo" 
              className="logo" 
              onError={() => setLogoError(true)}
            />
          ) : (
            <div className="logo-placeholder">NP</div>
          )}
          <h1>Welcome to <span className="app-name">Nepal Police App</span></h1>
          <p className="subtitle">You can start by creating an account or by logging in our app.</p>
        </div>

        <div className="signin-section">
          <h2>Sign In</h2>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="input-group">
            <input
              type="email"
              name="email"
              placeholder="Email/Username"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <span className="input-icon user-icon">
              {/* Once you have the user SVG, we'll add it here */}
              üë§
            </span>
          </div>
          
          <div className="input-group">
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <span 
              className="input-icon password-icon"
              onClick={togglePasswordVisibility}
            >
              {/* Once you have the eye SVG, we'll add it here */}
              üëÅÔ∏è
            </span>
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input
                type="checkbox"
                name="rememberMe"
                checked={formData.rememberMe}
                onChange={handleChange}
              />
              Remember me
            </label>
            <a href="#" className="forgot-password">Forgot Password?</a>
          </div>

          <button type="submit" className="login-btn">Login</button>
          
          <div className="signup-section">
            <p>Don't have an account yet? <a href="#" className="signup-link">Sign Up</a></p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
