import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { incidentService } from '../services/api';
import './MyIncidents.css';

const MyIncidents = () => {
  const navigate = useNavigate();
  const [incidents, setIncidents] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filterOpen, setFilterOpen] = useState(false);
  const [filters, setFilters] = useState({
    status: 'all',
    dateRange: 'all'
  });

  useEffect(() => {
    fetchIncidents();
  }, []);

  const fetchIncidents = async () => {
    try {
      const response = await incidentService.getAllIncidents();
      setIncidents(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch incidents');
      setLoading(false);
    }
  };

  const handleBack = () => {
    navigate('/home');
  };

  const handleIncidentClick = (incident) => {
    navigate(`/incident-details/${incident._id}`, { 
      state: { 
        incident,
        fromMyIncidents: true
      } 
    });
  };

  const getIncidentIcon = (type) => {
    switch (type.toLowerCase()) {
      case 'explosives/weapons':
        return 'ðŸ’£';
      case 'theft':
        return 'ðŸ•µï¸';
      case 'others':
        return 'ðŸ“¦';
      case 'fraud':
        return 'ðŸ’°';
      case 'domestic violence':
        return 'ðŸ ';
      default:
        return 'â“';
    }
  };

  // Convert AD date to BS date
  const convertToBS = (adDate) => {
    const ad = new Date(adDate);
    const adYear = ad.getFullYear();
    const adMonth = ad.getMonth() + 1;
    const adDay = ad.getDate();

    // Rough conversion (you might want to use a more accurate conversion library)
    let bsYear = adYear + 56;
    let bsMonth = adMonth;
    let bsDay = adDay + 16;
    
    // Adjust for month end
    if (bsDay > 32) {
      bsDay -= 32;
      bsMonth += 1;
    }
    if (bsMonth > 12) {
      bsMonth = 1;
      bsYear += 1;
    }

    return `${bsYear}-${String(bsMonth).padStart(2, '0')}-${String(bsDay).padStart(2, '0')}`;
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    
    const bsDate = convertToBS(dateString);
    return `${year}-${month}-${day} (${bsDate} B.S)`;
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'acknowledged':
        return 'green';
      case 'rejected':
        return 'red';
      case 'pending':
        return 'orange';
      default:
        return 'gray';
    }
  };

  const applyFilters = (incidents) => {
    return incidents.filter(incident => {
      // Filter by search query
      const matchesSearch = incident.type.toLowerCase().includes(searchQuery.toLowerCase());
      
      // Filter by status
      const matchesStatus = filters.status === 'all' || 
                           incident.status.toLowerCase() === filters.status.toLowerCase();
      
      // Filter by date range
      let matchesDate = true;
      if (filters.dateRange !== 'all') {
        const incidentDate = new Date(incident.createdAt);
        const today = new Date();
        const daysDiff = (today - incidentDate) / (1000 * 60 * 60 * 24);
        
        switch (filters.dateRange) {
          case 'week':
            matchesDate = daysDiff <= 7;
            break;
          case 'month':
            matchesDate = daysDiff <= 30;
            break;
          case 'year':
            matchesDate = daysDiff <= 365;
            break;
          default:
            matchesDate = true;
        }
      }
      
      return matchesSearch && matchesStatus && matchesDate;
    });
  };

  const filteredIncidents = applyFilters(incidents);

  return (
    <div className="my-incidents-container">
      <div className="incidents-header">
        <button className="back-button" onClick={handleBack}>
          <svg width="24" height="24" viewBox="0 0 24 24">
            <path d="M20 11H7.83l5.59-5.59L12 4l-8 8 8 8 1.41-1.41L7.83 13H20v-2z" fill="white"/>
          </svg>
        </button>
        <h1>My Incidents</h1>
        <button className="filter-button" onClick={() => setFilterOpen(!filterOpen)}>
          <svg width="24" height="24" viewBox="0 0 24 24">
            <path d="M3 17v2h6v-2H3zM3 5v2h10V5H3zm10 16v-2h8v-2h-8v-2h-2v6h2zM7 9v2H3v2h4v2h2V9H7zm14 4v-2H11v2h10zm-6-4h2V7h4V5h-4V3h-2v6z" fill="white"/>
          </svg>
        </button>
      </div>

      {filterOpen && (
        <div className="filter-menu">
          <div className="filter-section">
            <h4>Status</h4>
            <select 
              value={filters.status}
              onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value }))}
            >
              <option value="all">All</option>
              <option value="pending">Pending</option>
              <option value="acknowledged">Acknowledged</option>
              <option value="rejected">Rejected</option>
            </select>
          </div>
          <div className="filter-section">
            <h4>Date Range</h4>
            <select
              value={filters.dateRange}
              onChange={(e) => setFilters(prev => ({ ...prev, dateRange: e.target.value }))}
            >
              <option value="all">All Time</option>
              <option value="week">Last Week</option>
              <option value="month">Last Month</option>
              <option value="year">Last Year</option>
            </select>
          </div>
        </div>
      )}

      <div className="search-container">
        <input
          type="text"
          placeholder="Search"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input"
        />
      </div>

      {loading ? (
        <div className="loading">Loading incidents...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <div className="incidents-list">
          {filteredIncidents.map((incident) => (
            <div 
              key={incident._id} 
              className="incident-card"
              onClick={() => handleIncidentClick(incident)}
            >
              <div className="incident-icon">
                {getIncidentIcon(incident.type)}
              </div>
              <div className="incident-details">
                <h3>{incident.type}</h3>
                <div className="incident-info">
                  <p className="incident-id">
                    <strong>Incident Id</strong><br />
                    {incident._id}
                  </p>
                  <p className="reported-date">
                    <strong>Reported Date</strong><br />
                    {formatDate(incident.createdAt)}
                  </p>
                  <p className="status" style={{ color: getStatusColor(incident.status) }}>
                    <strong>Status</strong><br />
                    {incident.status}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyIncidents;
