import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { incidentService } from '../services/api';
import './IncidentDetails.css';

const IncidentDetails = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const [formData, setFormData] = useState({
    type: location.state?.incidentType || '',
    description: '',
    location: {
      coordinates: null,
      address: ''
    },
    media: {
      images: [],
      audio: [],
      video: []
    }
  });

  const [currentFileIndices, setCurrentFileIndices] = useState({
    images: 0,
    audio: 0,
    video: 0
  });

  const [previews, setPreviews] = useState({
    images: [],
    video: []
  });

  const handleBack = () => {
    // If we came from report incident page, go back there
    if (location.state?.fromReportIncident) {
      navigate('/report-incident');
    } else {
      // Otherwise go back to home
      navigate('/home');
    }
  };

  const handleDescriptionChange = (e) => {
    setFormData(prev => ({
      ...prev,
      description: e.target.value
    }));
  };

  const handleLocationSelect = async () => {
    try {
      // For demo purposes using Kathmandu coordinates
      const coordinates = [27.7172, 85.3240];
      const address = 'Kathmandu, Nepal';
      
      setFormData(prev => ({
        ...prev,
        location: {
          coordinates,
          address
        }
      }));
    } catch (error) {
      setError('Failed to get location. Please try again.');
    }
  };

  const handleFileUpload = (type, files) => {
    const allowedFiles = Array.from(files).filter(file => {
      const size = file.size / (1024 * 1024); // Convert to MB
      const maxSize = type === 'video' ? 50 : 10;
      return size <= maxSize;
    });

    if (allowedFiles.length < files.length) {
      setError(`Some files were too large. Maximum size: ${type === 'video' ? '50MB' : '10MB'}`);
      return;
    }

    const fileArray = allowedFiles.map(file => ({
      url: URL.createObjectURL(file),
      filename: file.name,
      size: file.size,
      file
    }));

    setFormData(prev => ({
      ...prev,
      media: {
        ...prev.media,
        [type]: [...prev.media[type], ...fileArray]
      }
    }));

    // Update previews for images and videos
    if (type === 'images' || type === 'video') {
      setPreviews(prev => ({
        ...prev,
        [type]: [...prev[type], ...fileArray.map(f => f.url)]
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!formData.location.coordinates) {
      setError('Please select a location');
      setLoading(false);
      return;
    }

    try {
      const formDataToSend = new FormData();
      
      // Add basic incident data
      formDataToSend.append('type', formData.type);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('reportedBy', 'user123'); // Add actual user ID here
      formDataToSend.append('location', JSON.stringify({
        type: 'Point',
        coordinates: formData.location.coordinates
      }));

      // Add media files
      Object.entries(formData.media).forEach(([type, files]) => {
        files.forEach((fileObj, index) => {
          if (fileObj.file) {
            formDataToSend.append(`${type}`, fileObj.file);
          }
        });
      });

      const response = await incidentService.createIncident(formDataToSend);
      console.log('Incident created:', response);
      navigate('/home', { state: { incidentCreated: true } });
    } catch (err) {
      setError(err.message || 'Failed to create incident. Please try again.');
      console.error('Error creating incident:', err);
    } finally {
      setLoading(false);
    }
  };

  const renderFilePreview = (type, file, index, total) => {
    const isImage = type === 'images';
    const isVideo = type === 'video';
    const currentIndex = currentFileIndices[type];
    const previewUrl = type === 'images' ? previews.images[currentIndex] : 
                      type === 'video' ? previews.video[currentIndex] : null;

    if (index !== currentIndex) return null;

    return (
      <div className="file-preview-container" key={index}>
        <div className="file-preview">
          {isImage && (
            <img src={previewUrl} alt={`Preview ${index + 1}`} />
          )}
          {isVideo && (
            <video src={previewUrl} controls />
          )}
        </div>
        <div className="file-counter">{index + 1}/{total}</div>
        <button
          type="button"
          className="delete-button"
          onClick={() => {
            setFormData(prev => ({
              ...prev,
              media: {
                ...prev.media,
                [type]: prev.media[type].filter((_, i) => i !== index)
              }
            }));
          }}
        >
          √ó
        </button>
      </div>
    );
  };

  return (
    <div className="incident-details-container">
      <div className="incident-details-card">
        <div className="incident-header">
          <button className="back-button" onClick={handleBack}>
          <svg width="24" height="24" viewBox="0 0 24 24">
            <path d="M20 11H7.83l5.59-5.59L12 4l-8 8 8 8 1.41-1.41L7.83 13H20v-2z" fill="white"></path>
            </svg>
          
          </button>
          <h2>{formData.type || 'Report Incident'}</h2>
        </div>

        <form className="incident-form" onSubmit={handleSubmit}>
          <textarea
            className="description-input"
            placeholder="Enter Description"
            value={formData.description}
            onChange={handleDescriptionChange}
            required
          />

          <button
            type="button"
            className="location-selector"
            onClick={handleLocationSelect}
          >
            <span className="icon">üìç</span>
            {formData.location.address || 'Choose Incident Location'}
          </button>

          <div className="upload-sections">
            <div className="upload-section">
              <label>
                <span className="upload-icon">üñºÔ∏è</span>
                Images (Max 10MB each)
                <input
                  type="file"
                  accept="image/*"
                  multiple
                  onChange={(e) => handleFileUpload('images', e.target.files)}
                />
              </label>
            </div>

            <div className="upload-section">
              <label>
                <span className="upload-icon">üéµ</span>
                Audio (Max 10MB each)
                <input
                  type="file"
                  accept="audio/*"
                  multiple
                  onChange={(e) => handleFileUpload('audio', e.target.files)}
                />
              </label>
            </div>

            <div className="upload-section">
              <label>
                <span className="upload-icon">üé•</span>
                Video (Max 50MB each)
                <input
                  type="file"
                  accept="video/*"
                  multiple
                  onChange={(e) => handleFileUpload('video', e.target.files)}
                />
              </label>
            </div>
          </div>

          <div className="preview-section">
            {formData.media.images.map((file, index) => (
              renderFilePreview('images', file, index, formData.media.images.length))
            )}
          </div>

          <div className="preview-section">
            {formData.media.video.map((file, index) => (
              renderFilePreview('video', file, index, formData.media.video.length))
            )}
          </div>

          {error && <div className="error-message">{error}</div>}

          <button
            type="submit"
            className="submit-button"
            disabled={loading}
          >
            {loading ? 'Submitting...' : 'Submit'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default IncidentDetails;
